import React  from 'react';
import './form.css';

export class Form extends React.Component{

    static  i=0;

constructor(props){
    super(props);

    this.state={ 
                items:[],
                currentItem:{
                         task:'',
                          key:''
                      }
}
}
    
    addTask(e){
const newtask=this.state.currentItem;
const items=[...this.state.items,newtask];

this.setState({
    items:items,

    currentItem:{
        task:'',
        key:''
    }
})
    }
    takeInput(e){
        this.setState({
            currentItem:{
                task: e.target.value,
                key: this.i++
            }
        })
    }
      render(){
          return (<form onSubmit={this.addTask}>
                    <input value={this.state.currentItem.task} onChange={this.takeInput}></input>
                    <button type="submit">Add</button>
          </form>
          
          )
      }
      
  }
  
export default Form;
